public class Aufgabe2 extends PurpleJExercise {

    public String title = "Aufgabe 2";
    public String description = "Füge der Klasse Huhu eine Methode `bla()` hinzu.";

    public void onInteractive() {
        try {
            Class<?> huhuClass = Class.forName("Huhu");
            try {
                huhuClass.getDeclaredMethod("bla");
                setCompleted();
            } catch (NoSuchMethodException e) {
                // Methode bla() existiert nicht
                setStatus("Methode `bla` nicht gefunden.");
            }
        } catch (ClassNotFoundException e) {
            // If the class is not found, invoke setStatus(String) with a German message
            setStatus("Bitte löse zuerst Aufgabe 1");
        }
    }
}

